import React, {Component} from "react"
import Logo from './static/logo.svg';
import './styles/TeamComp.css';
import TeamLeft from './TeamLeft';
import TeamDetails from './TeamDetails';
import members from '../teamMembers';
// import Swiper core and required components
import Swiper, { Navigation, Pagination, Scrollbar, A11y, EffectCoverflow, Autoplay } from 'swiper';
// import { Swiper, SwiperSlide } from 'swiper/react';
import swiper from 'swiper/bundle';


// Import Swiper styles
import 'swiper/swiper.scss';
import 'swiper/components/effect-coverflow/effect-coverflow.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';

// install Swiper components
// SwiperCore.use([Navigation, Pagination, Scrollbar, A11y, EffectCoverflow, Autoplay]);

// const useStyles = makeStyles({
//     SwiperStyle: {
//         paddingTop: '50px',
//         paddingBottom: '50px',
//         width: '100%',
//     },
// });

class FourZeroFour extends Component{
  constructor(props) {
    super(props);
    this.state = {
      team_members: members, //team members array which is initially set to all members
      curr_member:0 //member index in a certain team
    }
    this.setTeamMembers= this.setTeamMembers.bind(this);
    this.setCurrMember= this.setCurrMember.bind(this);
    // const classes = useStyles();
  }

  setTeamMembers(_team_members) {
    this.setState({team_members: _team_members});
  }

  setCurrMember(_curr_member) {
    this.setState({curr_member: _curr_member});
  }

  handleNext = () => {
    if(this.state.curr_member<this.state.team_members.length-1){
      this.setState({curr_member: this.state.curr_member+1})
    }
  }

  handlePrev = () =>{
    if(this.state.curr_member>0){
      this.setState({curr_member: this.state.curr_member-1})
    }
  }

  componentDidMount () {
    var swiper = new Swiper('.swiper-container', {
          effect: 'coverflow',
          grabCursor: true,
          centeredSlides: true,
          slidesPerView: 15,
          coverflowEffect: {
            rotate: 20,
            stretch: 0,
            depth: 200,
            modifier: 1,
            slideShadows: true,
          },
          loop: true,
          autoplay: {
            delay: 1000,
            disableOnInteraction: false,
            },
         });
  }

  render(){
    return(
      <>
        <div className="header">
          <img src={Logo} className="logo" alt="" srcset=""/>
          DSC REVA
        </div>
        <div className="row">
          <div className="col m3 l3 hide-on-small-only"><TeamLeft members={members} setTeamMembers={this.setTeamMembers} setCurrMember={this.setCurrMember}/></div>
          <div className="col m9 l9"><TeamDetails member={this.state.team_members[this.state.curr_member]} handlePrev={this.handlePrev} handleNext={this.handleNext}/></div>
        </div>  
        <div className="bottom_slider">
            {/* <Swiper
              // spaceBetween={0}
              // slidesPerView={10}
              // scrollbar={{ draggable: true }}
              // onSwiper={(swiper) => console.log(swiper)}
              // loop={true}
              //   autoplay={{
              //       delay: 500,
              //       disableOnInteraction: false
              //   }}
              effect= 'coverflow'
              grabCursor= {true}
              centeredSlides= {true}
              slidesPerView= 'auto'
              coverflowEffect = {{
                rotate: 20,
                stretch: 0,
                depth: 200,
                modifier: 1,
                slideShadows: true,
              }}
          loop= {true}
          autoplay= {{
            delay: 500,
            disableOnInteraction: false,
            }}
            >
            {members.map(function (member, index) {
              return <SwiperSlide><img width="150" class="member-image-slider" src={require(`${member.image_url}`)} key={index}/></SwiperSlide>;
            })}
            </Swiper>
            <style jsx global>{`
                    .swiper-container {
                        width: 100%;
                        padding-top: 50px;
                        padding-bottom: 50px;
                    }
           `}</style> */}

           <div class="swiper-container">
        <div class="swiper-wrapper">
          {members.map(function (member, index) {
              return <div class="swiper-slide"><img class="member-image-slider" src={require(`${member.image_url}`)} key={index}/></div>
            })}
        </div>
        <div class="swiper-pagination"></div>
      </div>
        </div>
      </>
    );
  }

}


export default FourZeroFour;
